window.maso = [



    {nazev: "Brambory plněné mletým masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- brambory<br>- mleté maso<br>- cibule<br>- česnek<br>- sýr<br>- slanina<br>- vejce<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Brambory dobře omyjeme a vaříme 15 min v mírně osolené vodě. Nesmí se uvařit příliš do měkka.<br><br>Mleté maso, na jemno nakrájenou cibulku, vajíčko, pepř, sůl a česnek pořádně smícháme.<br><br>Bramborám vykrojíme střed a naplníme směsí z masa.<br><br>Na vrch pokládáme plátek anglické slaniny a syr, můžeme zasypat jarní cibulkou.<br><br>Pěkně to vyskládáme do zapékací mísy, zakápneme olejem a trochou vody, můžeme přidat cibuli, kukuřici, klobásu a vykrájené zbytky brambor.<br><br>Vše dáme na 20 min zapéct.", obrazek: "<img src='../../img/maso/Brambory-plnene-mletym-masem.webp'>" },
    {nazev: "Buci", ingredience: "<b><u>Ingredience:</u></b><br><br>- hladká mouka<br>- voda<br>- mleté maso<br>- sůl<br>- pepř<br>- cibule", postup: "<b><u>Postup přípravy:</b></u><br><br>Uhněteme si těsto s vodou tak, aby se moc nelepilo.<br><br>Mleté maso, sůl, pepř a nakrájenou cibuli najemno smícháme.<br><br>Z těsta si nachystáme placičky, vložíme kus masové směsi a pořádně uzavřeme do tvaru škeble a smažíme do zlatova.", obrazek: "<img src='../../img/maso/Buci.webp'>" },
    {nazev: "Buřtguláš", ingredience: "<b><u>Ingredience:</u></b><br><br>- buřt nebo točený salám<br>- brambory<br>- cibule<br>- sladká paprika<br>- sůl<br>- pepř<br>- olej<br>- jíška", postup: "<b><u>Postup přípravy:</b></u><br><br>Na rozpálený olej přidáme nakrájenou cibuli a lehce osmažíme a zaprášíme paprikou.<br><br>Přidáme nakrájený buřt, orestujeme a zalijeme vodou.<br><br>Můžeme přidat bujón.<br><br>Dáme si vařit brambory, které pak přidáme k buřtu.<br><br>Dochutíme a zahustíme.", obrazek: "<img src='../../img/maso/Burtgulas.webp'>" },
    {nazev: "Čína", ingredience: "<b><u>Ingredience:</u></b><br><br>- 3 kuřecí prsa<br>- 1 červená kapie<br>- 1 cibule<br>- 1/2 konzervy kukuřice<br>- sterilované žampiony<br>- sojová omáčka<br>- koření na Čínu<br>- kari<br>- lžička mletého zázvoru<br>- pepř<br>- sůl<br>- 1 vrchovatá lžíce škrobu (Solamyl)<br>- 2 stroužky česneku<br>- olivový olej", postup: "<b><u>Postup přípravy:</b></u><br><br>Omyté a očištěné maso pokrájíme na nudličky a dáme do misky, posypeme kořením na čínu, pepřem, zázvorem a zasypeme škrobem.<br><br>Vše promícháme, aby se maso obalilo.<br><br>Cibuli a papriku pokrájíme na proužky.<br><br>Dáme rozehřát olej do hlubší pánve a zprudka opékáme kuřecí maso, když máme maso opečené ze všech stran, přidáme zeleninu, žampiony (bez nálevu) a kukuřici.<br><br>Podlijeme sojovou omáčkou cca 3 lžíce a trochou vody cca 2 lžíce, přidáme kari a česnek, promícháme a necháme zeleninu změknout cca 10-15 min.<br><br>Můžeme dochutit solí nebo čili (sojovou omáčku dáváme dle chuti, zeleninu můžeme použít dle libosti).<br><br>Podáváme nejlépe s rýží.", obrazek: "<img src='../../img/maso/Cina.webp'>" },
    {nazev: "Čočka s klobásou", ingredience: "<b><u>Ingredience:</u></b><br><br>- 500 g čočky<br>- 3 lžíce hladké mouky<br>- 1-2 cibule<br>- párek (klobása)<br>- chleba<br>- olej<br>- sůl<br>- pepř<br>- ocet", postup: "<b><u>Postup přípravy:</b></u><br><br>Čočku naložíme přes noc v hrnci s voudou a ráno několikrát prolijeme čistou vodou.<br><br>Dáme čočku vařit. Po dovaření v případě potřeby slijeme přebytečnou vodu.<br><br>Nakrájíme cibuli a osmažíme ji na tuku dozlatova.<br><br>Vše zaprášíme moukou a zalijeme trochou vody scezené z čočky.<br><br>Do směsi přidáme též podle chuti ocet, sůl a pepř.<br><br>Vše povaříme a poté vmícháme do uvařené čočky.", obrazek: "<img src='../../img/maso/Cocka-SKlobasou.webp'>" },
    {nazev: "Dušená mrkev s masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- 1 cibule<br>- 1 mrkev<br>- brambory<br>- 2 lžíce hladké mouky<br>- 0,5 l zeleninového vývaru nebo bujón<br>- 0,5 l vody<br>- 2 lžíce kysané smetany<br>- 1 kg vepřové plece<br>- pepř<br>- 0,5 lžičky soli", postup: "<b><u>Postup přípravy:</b></u><br><br>Očištěnou mrkev nastrouháme na hrubo, mírně osolíme, podlijeme vodou a pod poklicí zvolna dusíme asi 30 min.<br><br>Kdo chce, přidá i hrášek.<br><br>Omyté maso nakrájíme na větší kousky, osolíme a opepříme, podlijeme vodou a v troubě upečeme doměkka.<br><br>Upečené maso vyjmeme, vypečenou šťávu přecedíme do kastrolu, na ní necháme zesklovatět drobně nakrájenou cibuli, zaprášíme moukou a krátce osmahneme.<br><br>Zalijeme vývarem, vložíme maso a dušenou mrkev, krátce povaříme a nakonec dochutíme cukrem a smetanou.<br><br>Dušenou mrkev s vepřovým masem podáváme s vařenými brambory.", obrazek: "<img src='../../img/maso/Dusena-Mrkev-SMasem.webp'>" },
    {nazev: "Dýňové rizoto", ingredience: "<b><u>Ingredience:</u></b><br><br>- 150 g dýně<br>- 220 g kuřecí prsa<br>- 1 balení mozzarelly<br>- sýr na strouhání<br>- 320 g rýže<br>- 1 cibule<br>- 1/2 l vývaru<br>- 1 lžíce oleje<br>- sůl<br>- pepř<br>- kurkuma", postup: "<b><u>Postup přípravy:</b></u><br><br>Mezitím si připravíme dýňové pyré. Dýni oloupeme, rozkojíme a zbavíme vnitřku. Poté ji nakrájíme na menší kostičky, dáme do hrnce a podlijeme troškou vody. Necháme pod pokličkou dusit, dokud nám dýně úplně nezměkne. Nakonec vodu slijeme a dýni rozmixujeme.Nyní si na pánvi rozpustíme ghí. Než se nám rozpustí, připravíme si kuřecí maso, které posypeme kořením na kuře. Poté ho smažíme z obou stran.Nakonec maso vytáhneme, na pánev dáme dýňové pyré a uvařenou rýži. Přidáme kurkumu, sůl, pepř a nastrouhanou mozzarellu. Vše promícháme. Hotové rizoto podáváme s kuřecím masem, rukolou a kousky parmazánu. ", obrazek: "<img src='../../img/maso/Dynove-Rizoto.webp'>" },
    {nazev: "Fazole s uzený masem a brambory", ingredience: "<b><u>Ingredience:</u></b><br><br>- bílé fazole<br>- smetana<br>- uzené maso<br>- brambor<br>- cibule<br>- bujón<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Přes noc necháme namočené fazole.<br><br>Několikrát propláchneme čistou vodou a dáme vařit.<br><br>Uvaříme si nakrájené brambory.<br><br>Cibuli najemno nakrájíme a dáme smažit, na chvilku přidáme nakrájené uzené maso na prohřátí.<br><br>Uvařené fazole podle potřeby slijeme a zalijeme smetanou, přidáme bujón a vaříme.<br><br>Dochutíme solí a pepřem (podravkou).", obrazek: "<img src='../../img/maso/Fazole-SUzeny-Masem-ABrambory.webp'>" },
    {nazev: "Francouzské brambory", ingredience: "<b><u>Ingredience:</u></b><br><br>- 1 kg brambor<br>- 250 g uzeného masa<br>- 1 velká cibule<br>- 4 vejce<br>- 400 ml 30% smetany<br>- sůl<br>- pepř<br>- máslo", postup: "<b><u>Postup přípravy:</b></u><br><br>Brambory oloupeme a pokrájíme na plátky (1cm). Uzené maso pokrájíme na kostičky, cibuli oloupeme a nakrájíme na kolečka. Zapékací formu vymažeme a na dno naskládáme vrstvu brambor, které osolíme a opepříme. Pokračujeme vrstvou masa, cibule a pak opět brambory, které osolíme a opepříme. Mísu dáme do předem vyhřáté trouby na 10minut a 180 °C.<br><br>V misce rozmícháme vejce a smetanu, osolíme, opepříme a pořádně prošleháme. Vaječnou směsí rovnoměrně zalijeme předpečené brambory a vše pečeme zhruba 30 minut.", obrazek: "<img src='../../img/maso/francouzske-brambory.webp'>" },
    {nazev: "Gnocchi se špenátem a kuřecím masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- gnocchi<br>- špenát<br>- smetana<br>- kuřecí prsa<br>- cibule<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Cibulku nakrájíme najemno a dáme smažit, přidáme maso nakrájené na kostky, opepříme a osolíme.<br><br>Listový špenát povaříme, přidáme smetanu a chvílu vaříme, poté přidáme osmažené maso s cibulkou.<br><br>Podle návodu na obalu uvaříme gnocchi.<br><br>Necháme okapat a pak promícháme s omáčkou.", obrazek: "<img src='../../img/maso/Gnocchi-Se-Spenatem-AKurecim-Masem.webp'>" },
    {nazev: "Halušky s brynzou", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>TĚSTO:</b><br>- 1,5 hrnku polohrubé mouky<br>- 7 brambor (střední velikosti)<br>- 1 lžička soli<br>- nebo prostě už připravené těsto v obchodě<br><br>- 200 g zakysané smetany<br>- 200 g brynzy<br>- 300 g anglické slaniny", postup: "<b><u>Postup přípravy:</b></u><br><br>Brambory oškrábeme a na jemném struhadle nastrouháme.<br><br>Osolíme a zaděláme s moukou do těsta (hodně záleží na vodnatosti brambor, mouku přidáváme opatrně).<br><br>Těsto by mělo být lehké a lehce lepivé.<br><br>Do vroucí osolené vody buď nožíkem z prkénka odkrajujeme, nebo stěrkou přes speciální cedník tlačíme malé nočky,<br>které jsou uvařené ve chvíli, kdy vyplavou na hladinu (vaříme na etapy).<br><br>Po vyjmutí je propláchneme krátce studenou vodou, dáme do mísy a zakryjeme<br>V misce si dohladka vymícháme brynzu se smetanou.<br><br>Na pánvičce vyškvaříme na kostky nakrájenou anglickou slaninu.<br><br>Na talíř servírujeme uvařené halušky, přelijeme brynzovou zálivkou a posypeme vyškvařenou slaninou.", obrazek: "<img src='../../img/maso/Halusky-SBrynzou.webp'>" },
    {nazev: "Hovězí guláš", ingredience: "<b><u>Ingredience:</u></b><br><br>- 800 g hovězí kližky<br>- 4 střední cibule<br>- 4 stroužky česneku<br>- 1 lžíce sladké papriky<br>- 1 lžíce gulášového koření<br>- 1 malý rajský protlak (kečup)<br>- lžička majoránky<br>- pepř<br>- sůl<br>- 3 lžíce sádla<br>- tmavá jíška (bobky)", postup: "<b><u>Postup přípravy:</b></u><br><br>Osmahneme cibuli, zaprášíme paprikou a přidáme pokrájené maso na kostky.<br><br>Necháme maso zatáhnout, osolíme, přidáme pepř, mletou papriku, gulášové koření, zamícháme a odstavíme cca na 5 minut z ohně.<br><br>Pak podlijeme horkou vodou (nebo vývarem z hovězího bujonu), přidáme protlak a vaříme do změknutí.<br><br>Do vroucího guláše nasypeme jíšku dle potřeby (houstne, až po chvilce míchání).<br><br>Přidáme prolisovaný česnek, majoránku a chvilinku povaříme.", obrazek: "<img src='../../img/maso/Hovezi-Gulas.webp'>" },
    {nazev: "Hovězí maso s dýňovou omáčkou a knedlíkem", ingredience: "<b><u>Ingredience:</u></b><br><br>- 800 g hovězího masa vhodného na pečení (např. hovězí zadní)<br>- 100 g slaniny nebo špeku na prošpikování masa<br>- 6 dl vody (záleží na velikosti masa)<br>- 2 dl bílého vína<br>- 4 ks kuličky nového koření<br>- 4 ks bobkové listy<br>- 2 ks mrkve<br>- 1 ks menší rajče<br>- 1 ks malý celer<br>- 1 ks citron<br>- 1/2 ks dýně Hokaido<br>- 1 kelímek smetany ke šlehání<br>- pepř<br>- sůl<br><br> NA HRNKOVÝ KNEDLÍK:<br>- 1 hrnek hrubé mouky<br>- 1 hrnek mléka<br>- 1 lžička soli<br>- 2 ks rohlíky<br>- 2 ks vejce<br>- bylinky dle chuti<br>- sádlo na vymazání hrnků", postup: "<b><u>Postup přípravy:</b></u><br><br>Omáčka je na bázi svíčkové, jen je přidána dýně. Hovězí maso nasolíme a prošpikujeme špekem, vložíme do pekáče a zasypeme drobně nakrájenou zeleninou dle výše uvedených surovin, přidáme také uvedené koření a podlijeme v poměru cca 1/3 vína a 2/3 vody, pečeme přikryté v troubě cca 2 hodiny. Upečené maso vyndáme, vybereme koření, zeleninu rozmixujeme a zjemníme smetanou, kterou necháme lehce projít varem, omáčku případně dochutíme solí, pepřem, vínem či octem. Knedlík připravíme smícháním uvedených surovin; hmotu nalijeme do hrníčků vymazaných sádlem do dvoutřetinové výšky, ponoříme do hrnce s vodou tak, aby voda sahala po ouška, přiklopíme poklicí a vaříme přesně 33 minut, poté jednoduše knedlíky vyklopíme a nakrájíme.", obrazek: "<img src='../../img/maso/Hovezi-maso-s-dynovou-omackou.webp'>" },
    {nazev: "Houbová omáčka s masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- maso (jakékoliv)<br>- 0,5 l drobně nakrájených hub <br>- cibule <br>- 0,5 dcl oleje <br>- smetana na vaření <br>- 0,5 l mléka <br>- 4 lžíce hladké mouky<br>- sůl <br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Na oleji necháme zesklovatět jemně nakrájenou cibuli.<br><br>Přidáme nakrájené maso na kostky, z prudka osmažíme a podlijeme vodou.<br><br>Nakrájené houby osolíme a dusíme asi 10-15 min.<br><br>Přidáme mouku a necháme několik minut smažit a poté přidáme k hotovému masu.<br><br>Zalijeme smetanou, mlékem a za stálého míchání přivedeme omáčku do varu.<br><br>Omáčku dochutíme pepřem a solí, příp. se může přidat i trochu citronové šťávy.<br><br>Pokud nám omáčka hodně zhoustla, tak rozředíme mlékem a vaříme ještě asi 5 min.", obrazek: "<img src='../../img/maso/Houbova-Omacka-SMasem.webp'>" },
    {nazev: "Hrachová kaše s klobásou", ingredience: "<b><u>Ingredience:</u></b><br><br>- hrách<br>- klobása<br>- sůl<br>- pepř<br>- majoránka<br>- cibule<br>- sádlo<br>- česnek", postup: "<b><u>Postup přípravy:</b></u><br><br>Hrách necháme přes noc naložený ve vodě poté několikrát propláchneme čistou vodou a dáme vařit.<br><br>Vodu z uvařeného hrachu slijeme bokem a hrách rozmixujeme. Kaši rozředíme slitou vodou, osolíme, opepříme, přidáme majoránku a utřený česnek.<br><br>Kaši promícháme a pokud by se zdála moc hustá, můžeme naředit ještě smetanou.<br><br>Cibuli s klobásou osmažíme na sádle a kaši jí omastíme.", obrazek: "<img src='../../img/maso/Hrachova-Kase-SKlobasou.webp'>" },
    {nazev: "Játra na cibulce", ingredience: "<b><u>Ingredience:</u></b><br><br>- 300 g jater <br>- cibule <br>- sůl <br>- pepř <br>- hladká mouka <br>- lžíce sádla <br>- masox", postup: "<b><u>Postup přípravy:</b></u><br><br>Najemno nakrájenou cibulku zpěníme na sádle, přidáme pokrájená játra, osolíme, opepříme a osmahneme.<br><br>Osmažená játra zaprášíme moukou, zalijeme vodou a necháme dusit.<br><br>Pak přidáme masox, necháme ještě chvíli povařit, pak omáčku zahustíme.", obrazek: "<img src='../../img/maso/Jatra-Na-Cibulce.webp'>" },
    {nazev: "Jitrnice", ingredience: "<b><u>Ingredience:</u></b><br><br>- jitrnice<br>- brambory", postup: "<b><u>Postup přípravy:</b></u><br><br>Jitrnici osmažíme na pávni, ne moc na prudko, jinak praskne. Uvaříme k tomu brambory a můžeme si dát i tatarku.", obrazek: "<img src='../../img/maso/Jitrnice.webp'>" },
    {nazev: "Kapusta s bramborem a masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- 1 kapusta<br>- 1/2 kg brambor<br>- maso dle chuti (hovězí, veprové, uzené)<br>- 2 cibule<br>- česnek<br>- sádlo<br>- hl. mouka<br>- pepř<br>- sůl", postup: "<b><u>Postup přípravy:</b></u><br><br>Kapustu nakrájíme na hrubo, cibule na jemno.<br><br>Brambory oloupeme, propereme a nakrájíme na kostky.<br><br>Česnek očistíme a nastrouháme na jemném struhadle.<br><br>Do většího hrnce dáme osmažit nakrájené cibule na sádle, přidáme kapustu, osolíme, opepříme a asi 5 minut dusíme pod poklicí.<br><br>Rozmícháme mouku ve vodě a přilijeme ke kapustě.<br><br>Maso nakrájíme na kostky, ozmažíme a hotové přihodíme do hrnce s kapustou, pokud to není uzené, to bych volila plátky.<br><br>Odstavíme a přimícháme česnek.", obrazek: "<img src='../../img/maso/Kapusta-SBramborem-AMasem.webp'>" },
    {nazev: "Karbanátky s bramborem", ingredience: "<b><u>Ingredience:</u></b><br><br>- balení mletého masa (mix)<br>- česnek<br>- menší cibule<br>- strouhanka<br>- vejce<br>- sůl<br>- pepř<br>- majoránka<br>- lžička hořčice", postup: "<b><u>Postup přípravy:</b></u><br><br>Cibuli nakrájíme najemno a všechny ingredience smícháme.<br><br>Uděláme placičky a ty pokládáme na rozpálenou pánvici s olejem.<br><br>Smažíme do zhnědnutí.", obrazek: "<img src='../../img/maso/Karbanatky.webp'>" },
    {nazev: "Knedlo-zelo-vepřo", ingredience: "<b><u>Ingredience:</u></b><br><br>- vepřové maso (nejlépe plecko nebo krkovice)<br>- zelí (nebo zavařené)<br>- knedlík<br>- grilovací koření<br>- sůl<br>- kmín<br>- česnek<br>- cibule", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso osolit, opepřit, posypat kmínem a důkladně ho ze všech stran potřeme česnekem.<br><br>Maso posaďte do pekáče na kostičky cibule, přidejte sádlo, podlijte hrnkem horké vody a pečte do změknutí asi 1,5 hodiny.<br><br>Nakrouháme syrové zelí, dáme do hrnce, posolíme, pokmínujeme dle chuti a zamícháme.<br><br>Zavařené jen ohřejeme.<br><br>Upečené maso vyjměte z pekáče a nechte ho patnáct až dvacet minut odpočinout.<br><br>Po přibližně 15 min dušení zaprašte zelí hrubou moukou a znovu asi patnáct minut vařte na mírném plameni.<br><br>Pekáč, ve kterém jste pekli maso postavte na sporák a zahřívejte.<br><br>Vypečenou šťávu s kousky cibule nechte lehce odpařit, pak vsypte mouku a trochu ji osmahněte.<br><br>Zalijte 200 ml vody a zhruba patnáct minut vařte.", obrazek: "<img src='../../img/maso/Knedlo-Zelo-Vepro.webp'>" },
    {nazev: "Krevetové rizoto", ingredience: "<b><u>Ingredience:</u></b><br><br>- 350 g kulatozrnné rýže<br>- 3 lžíce olivového oleje<br>- 20 g jarní cibulky<br>- krevety<br>- 100 rajčatové passaty<br>- sůl<br>- pepř<br>- olej", postup: "<b><u>Postup přípravy:</b></u><br><br>Rýži dejte vařit podle návodu na obalu.<br><br>Ke konci vaření rýže rozpalte olej v pánvi, přidejte jarní cibulku, krátce ji orestujte,<br>vsypte krevety a zprudka je 3 min opékejte.<br><br>Vmíchejte passatu, dolaďte solí a pepřem.<br><br>Rýži sceďte a vmíchejte do pánve ke krevetám.", obrazek: "<img src='../../img/maso/Krevetove-Rizoto.webp'>" },
    {nazev: "Krémové lečo", ingredience: "<b><u>Ingredience:</u></b><br><br>- 6 ks rajčata<br>- 2 ks cibule<br>- 4 ks paprika zelená<br>- olej na smažení<br>- 2 lžíce rajský protlak<br>- 4 ks vejce<br>- 1 hrst pažitka nakrájená<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Rajčata dejte do mísy, zalijte vroucí vodou, do každého lehce píchněte nožem, aby se narušila slupka, a nechte asi 10 minut odstát. Poté rajčata slijte, oloupejte, zbavte dužniny a pokrájejte na kousky.<br><br>Cibuli nakrájejte na tenké klínky, papriky očistěte a pokrájejte na kousky velikosti sousta. Na pánvi rozehřejte asi lžíci oleje a začněte restovat cibuli a papriku na mírnějším ohni, asi 8 minut, zelenina by měla lehce změknout. Přidejte protlak a restujte asi 2 minuty, aby se rozvoněl. Teď vmíchejte rajčata a směs duste asi 5 minut, aby se zbavila přebytečné vody.<br><br>V misce vidličkou dobře rozmíchejte vejce, vlijte je k zelenině a pánev odstavte z ohně. Vejce pomalu vmíchejte do zeleninového základu, postačí jim teplo z horké zeleniny. Směs míchejte, dokud nebude krémová, kompaktní a vejce se nespojí se zeleninovým základem. Dochuťte solí a pepřem, ozdobte pažitkou.", obrazek: "<img src='../../img/maso/Kremove-leco.webp'>" },
    {nazev: "Krkovice v pivní marinádě", ingredience: "<b><u>Ingredience:</u></b><br><br>- 4 plátky vepřové krkovice<br>- 400 g zelených fazolek<br>- sůl<br>- 200 g anglické slaniny nakrájené na plátky<br><br><b>&nbsp; &nbsp;MARINÁDA:</b><br>- 5 lžic olivového oleje<br>- 200 ml nealkoholického piva Birell<br>- 2 lžíce citronové šťávy<br>- lžička hořčice<br>- 4 stroužky česneku<br>- lžička soli<br>- lžíce čerstvého tymiánu<br>- lžička oregana", postup: "<b><u>Postup přípravy:</b></u><br><br>Důkladně prošlehejte všechny ingredience na marinádu a naložte do ní krkovici.<br><br>Nechte alespoň hodinu uležet, nejlépe přes noc.<br><br>Poté grilujte 15 min z obou stran.<br><br>Fazolkám odřízněte stopky a 1-2 min nechte přejít varem v osolené vodě.<br><br>Vždy několik fazolek omotejte plátkem anglické slaniny do svazečku a pečte 4 min na grilu.", obrazek: "<img src='../../img/maso/Krkovice-Na-Marinade.webp'>" },
    {nazev: "Kuře na paprice", ingredience: "<b><u>Ingredience:</u></b><br><br>- kuřecí prsa<br>- 250 ml smetana<br>- cibule<br>- sladká paprika<br>- sůl<br>- hladká mouka<br>- masox", postup: "<b><u>Postup přípravy:</b></u><br><br>Dáme smažit cibuli, zaprášíme paprikou, přidáme kousky kuřete a zalijeme vodou, přidáme masox, osolíme.<br><br>Když je maso měkké, vyjmeme ho a necháme trochu vychladit.<br><br>Připravíme záklechtku (mouku se smetanou) a omáčku zahustíme.<br><br>Asi 10 min provařujeme na mírném plameni s občasným mícháním.<br><br>Mezitím obereme maso z kostí a přidáme do hotové omáčky.<br><br>Podáváme s knedlíkem nebo s těstovinami.", obrazek: "<img src='../../img/maso/Kure-Na-Paprice.webp'>" },
    {nazev: "Kuřecí kapsa", ingredience: "<b><u>Ingredience:</u></b><br><br>- celá kuřecí prsa<br>- sušená rajčata<br>- sýr<br>- sůl<br>- pepř<br>- cibule<br>- brambor<br>- mléko (smetana)", postup: "<b><u>Postup přípravy:</b></u><br><br>Celá prsa nařízneme, jako na jeden velký plátek, vložíme sušené rajče, plátek sýra, zabalíme a párátkama uzavřeme.<br><br>Posolíme, opřepříme a smažíme ze všech stran.<br><br>Dáme vařit brambory, klidně rozvařit, po uvaření našťoucháme, zalijeme mlékem (smetanou).<br><br>Osolíme, přidáme osmaženou cibulku a podáváme s kuřecí kapsou.", obrazek: "<img src='../../img/maso/Kureci-Kapsa.webp'>" },
    {nazev: "Kuřecí na kari s mandarinkou", ingredience: "<b><u>Ingredience:</u></b><br><br>- kuřecí prsa<br>- smetana<br>- cibule<br>- kari<br>- sůl<br>- pepř<br>- konzervované mandarinky<br>- solamyl", postup: "<b><u>Postup přípravy:</b></u><br><br>Nadrobno pokrájenou cibulkou dáme smažit spolu na kosty nakrájeným kuřecím masem, které osolíme a opepříme.<br><br>Když cibulka zesklovatí, přidáme trošku kari a povaříme.<br><br>Když maso změkne, přilijeme smetanu a přidáme konzervované mandarinky.<br><br>Na závěr zahustíme solamylem.<br><br>Podáváme s hranolkama nebo s rýží.", obrazek: "<img src='../../img/maso/Kureci-Na-Kari.webp'>" },
    {nazev: "Kuřecí prsa plněná rajčaty a sýrem", ingredience: "<b><u>Ingredience:</u></b><br><br>- 600 g kuřecí prsní řízek <br>- 8 ks sušená rajčata v oleji <br>- 150 g pastevecký sýr <br>- 15 ml panenský olivový olej<br>- 2 stroužky česneku<br>- pesto<br>- polníček<br>- čerstvá bazalka<br>- sůl<br>- pepř ", postup: "<b><u>Postup přípravy:</b></u><br><br>Předehřejte troubu na 190 °C. Do kuřecích prsou vykrojte nožem opatrně kapsu. Každé osolte, zevnitř a zvenku. Česnek prolisujte a vetřete do masa. Poté přidejte pesto (pozor, nejdřív ho nadávkujte lžičkou a poté roztírejte, aby nedošlo ke kontaminaci pesta ve skleničce syrovým masem), polníčkem a sušená rajčata. Na závěr do prsou vložte kousky sýra. V případě potřeby zajistěte otvor párátky. Na pánvi rozehřejte olivový olej. Kuře zatáhněte z obou stran dozlatova. Pak přendejte prsa na plech (případně nechte v pánvi, pokud je žáruvzdorná) a vložte do trouby. Pečte, dokud nebude vnitřní teplota kuřecích prsou 74 °C. To trvá asi 16 až 18 minut v závislosti na velikosti.n Podávejte zasypané čerstvou bazalkou.", obrazek: "<img src='../../img/maso/Kureci-Prsa-Plneny-Rajcaty.webp'>" },
    {nazev: "Kuřecí prsa s pečenou zeleninou", ingredience: "<b><u>Ingredience:</u></b><br><br>- 700 g kuřecích řízků<br>- 2 lžičky koření ras el hanout<br>- 1 batát<br>- 1 cuketu<br>- 2 cibule<br>- 4 stroužky česneku<br>- 1 červenou papriku<br>- 1 žlutou papriku<br>- olej<br>- 1 lžičku sušeného tymiánu<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Zeleninu nakrájíme na kousky. Rozložíme ji na plech nebo grilovací tácek.<br><br>Osolíme, opepříme a smícháme s tymiánem. Zakápneme olejem a pečeme v troubě nebo na grilu. V průběhu pečení občas promícháme.  <br>Maso nakrájíme na plátky, osolíme, opepříme a posypeme kořením.<br><br>Dáme do misky, zalijeme trochou oleje, promícháme a necháme chvilku stát.<br><br>Pak ho pečeme na grilu nebo grilovací pánvi. Můžeme podávat s pečivem nebo bramborem. ", obrazek: "<img src='../../img/maso/Kureci-prsa-s-pecenou-zeleninou.webp'>" },
    {nazev: "Kuřecí shawarma", ingredience: "<b><u>Ingredience:</u></b><br><br>- 6 stroužků česneku<br>- lžička soli<br>- lžíce strouhaného čerstvého zázvoru<br>- 4 kuřecí stehenní řízky<br><br><b>&nbsp; &nbsp;KOŘENÍCÍ SMĚS:</b><br>- lžíce římského kmínu<br>- lžíce mletého koriandru<br>- lžička kurkumy<br>- špetka kajenského pepře<br>- špetka skořice<br><br><b>&nbsp; &nbsp;SALÁT:</b><br>- půlka salátové okurky<br>- 2–3 rajčata<br>- žlutá paprika<br>- 1/4 červené cibule<br>- hrst bylinek – petržel, máta, koriandr<br>- lžíce olivového oleje<br>- citron<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Česnek utřete se solí, vmícháme zázvor a všechno koření a důkladně vetřete do kuřecího masa.<br><br>Zprudka opečeme z obou stran na pánvi na oleji nebo ogrilujeme v troubě.<br><br>Okurku i se slupkou, rajčata, papriku a cibuli nakrájejte na drobné kostičky.<br><br>Přidáme nasekané bylinky, zalijte lžící oleje, lžící citronové šťávy a promíchejte.<br><br>Nakonec dochuťte citronovou kůrou, solí a pepřem.<br><br>Kuřecí maso nakrájejte na proužky a podávejte se zeleninovým salátem.", obrazek: "<img src='../../img/maso/Kureci-Shawarma.webp'>" },
    {nazev: "Kuřecí steak na ledovém salátu", ingredience: "<b><u>Ingredience:</u></b><br><br>- kuřecí prsa<br>- ledový salát<br>- 2 strožky česneku<br>- 3 lžíce bílého jogurtu<br>- 3 lžíce majonézy<br>- lžička hořčice", postup: "<b><u>Postup přípravy:</b></u><br><br>Celá kuřecí prsa osmažíme.<br><br>Ledový salát nakrájíme.<br><br>Vytvoříme si dip z jogurtu, majonezy, hořčice a nastrouhaného česneku.<br><br>Salát pokládáme na talíř, na to položíme prso a zalijeme dipem.", obrazek: "<img src='../../img/maso/Kureci-Steak-Na-Ledovem-Salatu.webp'>" },
    {nazev: "Kuřecí stripsy", ingredience: "<b><u>Ingredience:</u></b><br><br>- kuřecí maso<br>- kukuřičné lupínky<br>- hladká mouka<br>- vajíčko<br>- sůl<br>pepř <br>koření dle chuti", postup: "<b><u>Postup přípravy:</b></u><br><br>Kuřecí maso nakrájíme na nudličky. Osolíme, opepříme, případně přidáme chilli či jiné koření dle chuti.<br><br>Maso obalíme v mouce, vajíčku a nakonec v nadrcených kukuřičných lupínkách.<br><br>Smažíme dozlatova.", obrazek: "<img src='../../img/maso/kureci-stripsy.webp'>" },
    {nazev: "Masové koule na fazolích", ingredience: "<b><u>Ingredience:</u></b><br><br>- 200 g mletého vepřového masa<br>- 3 brambory<br>- červené fazole v plechu<br>- 2 cibule<br>- 2 stroužky česneku<br>- malý rajský protlak<br>- oregano<br>- sůl<br>- pepř<br>- bazalka", postup: "<b><u>Postup přípravy:</b></u><br><br>Mleté maso osolíme a opepříme. Uděláme menší kuličky (vlašský ořech).<br><br>Cibuli nakrájíme na kostičky, česnek na plátky a společně s kuličkami vše orestujeme na pánvi.<br><br>Poté přidáme oloupané brambory nakrájené na kostičky a fazole.<br><br>Zalijeme 0,5 litrem vody. a dusíme cca 15 minut.<br><br>Poté přidáme rajčatový protlak, podle chuti osolíme, opepříme, přidáme oregano a špetku bazalky.<br><br>Necháme trochu prohřát a podle potřeby zředíme vodou.", obrazek: "<img src='../../img/maso/Masove-Koule-Na-Fazolich.webp'>" },
    {nazev: "Mini tortilly s dipy", ingredience: "<b><u>Ingredience:</u></b><br><br>- 4 celozrnné tortilly<br>- 500 g kuřecích prsních řízků<br>- 1 cuketa<br>- 1 cibule<br>- hrst sušených rajčat<br>- 2 lžičky koření ras el hanout<br>- sůl<br>- pepř<br>- olej<br><b>&nbsp; &nbsp;RAJČATOVÝ DIP:</b><br><br>- 100 g loupaných rajčat<br>- 1 chilli paprička<br>- 1 stroužek česneku<br>- sůl<br>- pepř<br><b>&nbsp; &nbsp;SMETANOVÝ DIP:</b><br><br>- 1 kelímek zakysané smetany<br>- 1 stroužek česneku<br>- 1 lžíce nasekané pažitky<br>- sůl<br>- pepř<br><b>&nbsp; &nbsp;AVOKÁDOVÝ DIP: </b><br><br>- 1 zralé avokádo<br>- 1 rajče<br>- 1 lžíce citronové šťávy<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Na dipy smícháme v misce všechny uvedené suroviny.<br><br>Maso nakrájíme na kousky, osolíme, opepříme a posypeme kořením.<br><br>Cuketu nakrájíme na kousky, sušená rajčata na proužky a cibuli na kolečka.<br><br>Na pánvi osmažíme cibuli s masem a poté přidáme cuketu a vše společně opečeme. Nakonec do směsi přidáme sušená rajčata.<br><br>Na pracovní desku rozložíme torttily a na každou naneseme masovou směs a opatrně zatočíme a nabalíme do alobalu.<br><br>Po vychladnutí nakrájíme na válečky (nebo ne) a podáváme s dipem.", obrazek: "<img src='../../img/maso/Mini-tortilly-s-dipy.webp'>" },
    {nazev: "Nádivka", ingredience: "<b><u>Ingredience:</u></b><br><br>- 3 středně velké bagety (nebo 10 rohlíků - nakrájet na kostky)<br>- 100 ml ohřátého mléka<br>- 3-4 vejce<br>- 200 g šunky v kuse (nebo játra, kuřecí maso, houby)<br>- čerstvá petrželová nať (nasekat nadrobno)<br>- lžička vegety<br>- máslo<br>- strouhanka", postup: "<b><u>Postup přípravy:</b></u><br><br>Pečivo smícháme s cca 100 ml mléka a s vajíčkem, nejlépe se to všechno míchá rukou.<br><br>Dle potřeby přidáme ještě mléko.<br><br>Přidáme šunku, bylinky, vegetu a sůl podle chuti a opět pořádně promícháme.<br><br>Středně velký plech si vymažeme máslem a vysypeme strouhankou.<br><br>Na plech si rozložíme rovnoměrně směs a navrch poklademe ještě malé kousky másla.<br><br>Pečeme v předem předehřáté troubě 170-180˚C asi 30 min.<br><br>Podáváme třeba s kuřetem a rýží.", obrazek: "<img src='../../img/maso/Nadivka.webp'>" },
    {nazev: "Pečené kuře", ingredience: "<b><u>Ingredience:</u></b><br><br>- kuře (prsa, křídla, stehna)<br>- grilovací koření<br>- med<br>- olej<br>- cibule<br>- česnek<br>- chilli<br>- rýže", postup: "<b><u>Postup přípravy:</b></u><br><br>Připravíme si směs: olej, grilovací koření, chilli, sojovou omáčku a jednu velkou lžíci medu.<br><br>Vše smícháme a naložíme do toho maso.<br><br>Cibuli a česnek nakrájíme, vložíme do zapékací mísy, do které vložíme naložené maso i se směsí a trošku vody.<br><br>Vše pečeme do zlatova a popřípadě ještě podléváme, pokud se maso neodděluje od kosti.<br><br>Podáváme s rýží.", obrazek: "<img src='../../img/maso/Pecene-Kure.webp'>" },
    {nazev: "Plněné knedlíky uzeným masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- 400 g vařeného uzeného masa (libový bůček, krkovice nebo plec) <br>- lžíce sádla <br>- cibule <br>- 750 g brambor (kupovaná směs v sáčku na knedlíky) <br>- 250 g hrubé mouky <br>- 100 g dětské krupičky <br>- velké vejce (nebo 2 malá) <br>- sůl <br>- dušené kysané zelí a osmažená cibule k podávání", postup: "<b><u>Postup přípravy:</b></u><br><br>Vařené uzené maso nakrájejte na kostičky.<br><br>Na rozpuštěném sádle osmahněte dorůžova cibuli, přimíchejte uzené, ještě chvíli opékejte pro zvýraznění chuti a nechte vychladnout.<br><br>Vychladlé brambory oloupejte a najemno nastrouhejte nebo prolisujte přímo na vál.<br><br>Přidejte prosátou mouku, krupičku, vejce, sůl a rychle zpracujte vláčné tužší těsto.<br><br>Těsto nenechávejte odpočívat a zadělávejte ho jen krátce a co nejrychleji, jinak začne řídnout.<br><br>Vyválejte váleček, rozkrájejte ho na stejně velké kousky (plátky) a ty v dlani zploštěte a roztáhněte nebo lehce rozválejte na placičky.<br><br>Každý plátek naplňte směsí uzeného masa s cibulkou, uzavřete a vytvarujete kulaté knedlíky.<br><br>Ihned je vkládejte do vroucí osolené vody a vařte podle velikosti 10–15 min.<br><br>Uvařené knedlíky vyjměte děrovanou lžící a podávejte s osmaženou cibulkou a zelím.", obrazek: "<img src='../../img/maso/Plnene-Knedliky-Uzenym-Masem.webp'>" },
    {nazev: "Plněné papriky", ingredience: "<b><u>Ingredience:</u></b><br><br>- 4 plátky toastového chleba<br>- 2-8 lžic mléka<br>- 2 cibule<br>- 400 g mletého masa (mix je lepší)<br>- 1 hrst nasekané petrželky<br>- 8-10 lezených paprik<br>- olej<br>- 1 lžičku mleté sladké papriky<br>- 500 g rajčat<br>- 4 bobkové listy<br>- 5 kuliček nového koření<br>- 5 uliček pepře<br>- 200 ml zakysané smetany<br>- 2 lžíce hl. mouky<br>- 1-2 lžíce cukru<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Toastový chléb nakrájíme na malé kostičky, zalijeme mlékem a necháme chvíli nasáknout.<br><br>Přidáme najemno nakrájenou cibuli, mleté maso, nasekanou petrželku a vejce. Směs ochutíme solí, pepřem a dobře promícháme rukama.<br><br>Paprikám odřízneme vršky, odstraníme semínka a naplníme masovou směsí.<br><br>V širším hrnci zpěníme najemno nakrájenou druhou cibuli, zasypeme paprikou a pár minut restujeme.<br><br>Přidáme pokrájená rajčata, asi 400 ml vody, koření a přivedeme k varu. Do směsi naskládáme papriky, přiklopíme a dusíme asi 30 minut.<br><br>Papriky vyndáme, vylovíme koření a omáčku rozmixujeme tyčovým mixérem.<br><br>V misce rozmícháme smetanu s moukou, vlijeme do omáčky a dobře promícháme, krátce prohřeje a dochutíme cukrem a solí.", obrazek: "<img src='../../img/maso/Plnena-paprika.webp'>" },
    {nazev: "Přírodní plátek masa", ingredience: "<b><u>Ingredience:</u></b><br><br>- maso <br>- koření na steak nebo grilovací koření<br>- příloha dle chuti", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso nakrájené na plátky okořeníme námi zvoleným kořením, trochu naklepeme nebo nařízneme (dle masa) a osmažíme.<br><br>Můžeme použít i zeleninu jako oblohu.", obrazek: "<img src='../../img/maso/Prirodn-Platek-Masa.webp'>" },
    {nazev: "Řízek s kaší", ingredience: "<b><u>Ingredience:</u></b><br><br>- maso <br>- brambory <br>- mléko <br>- máslo <br>- sůl <br>- vejce <br>- strouhanka <br>- hladká mouka", postup: "<b><u>Postup přípravy:</b></u><br><br>Libovolné maso, nakrájené na plátky, obalíme v trojobalu.<br><br>Dáme vařit brambory do osolené vody (klidně rozvaříme).<br><br>Slijeme vodu, přidáme trochu mléka nebo smetany, dvě lžíce másla a rozšťoucháme.<br><br>Jako oblohu můžeme přidat zeleninu dle libosti.", obrazek: "<img src='../../img/maso/Rizek-SKasi.webp'>" },
    {nazev: "Rajská omáčka", ingredience: "<b><u>Ingredience:</u></b><br><br>- hovězí maso<br>- rajčatový protlak<br>- bobkový list<br>- skořice<br>- cukr<br>- jíška", postup: "<b><u>Postup přípravy:</b></u><br><br>Dáme vařit maso s bobkovým listem a novým kořením.<br><br>Když se maso už rozpadá, vyndáme.<br><br>Protlak vlijeme do nového hrnce a přidáváme vývar z masa.<br><br>Zasepeme skořicí, přisladíme dle chuti a zahustíme jíškou.", obrazek: "<img src='../../img/maso/Rajska.webp'>" },
    {nazev: "Rizoto", ingredience: "<b><u>Ingredience:</u></b><br><br>- kuřecí prsa<br>- balíček mražené zeleniny<br>- 2 balíčky rýže<br>- mletá paprika <br>- cibule<br>- sůl<br>- tvrdý sýr", postup: "<b><u>Postup přípravy:</b></u><br><br>Rýži uvaříme podle návodu v osolené vodě.<br><br>Kuřecí prsa vložíme do hrnce s osolenou vodou a vaříme do měkka.<br><br>Cibuli nakrájíme najemno a necháme zpěnit, přidáme zeleninu, zalijeme trochou vody a necháme 10 min dusit.<br><br>Ke směsi přidáme rýži, kousek másla, špetku papriky, vegety, naporcované maso na kousky a necháme prohřát.<br><br>Zeleninové rizoto s kuřecím masem posypeme strouhaným sýrem.", obrazek: "<img src='../../img/maso/Rizoto.webp'>" },
    {nazev: "Rybí prsty", ingredience: "<b><u>Ingredience:</u></b><br><br>- rybí prsty<br>- brambory", postup: "<b><u>Postup přípravy:</b></u><br><br>Osmažíme na pánvi rybí prsty a uvaříme k tomu brambory.", obrazek: "<img src='../../img/maso/Rybi-Prsty.webp'>" },
    {nazev: "Řízek s bramborovým salátem", ingredience: "<b><u>Ingredience:</u></b><br><br>- maso na řízky<br>- strouhanka<br>- mouka<br>- 4 ks vejce<br>- mléko (pivo)<br>- 1 kg salátové brambory<br>- 6 ks sterilované okurky<br>- 4 ks mrkev<br>- sterilovaný hrášek<br>- 2 ks cibule<br>- celer<br>- 2 lžíce hořčice<br>- 1/2 bílého jogurtu<br>- 1 majonéza<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso dle výběru nakrájíme na plátky, osolíme, opepříme a naklepeme.<br><br>Takhle připravené řízky obalíme v trojobalu (mouka, vejce, strouhanka) do vajec ještě dávám trochu mléka a nebo pivo.<br><br>Brambory očistíme a dáme celé vařit.<br><br>Mrkev a celer okrájíme a dáme vařit klidně pospolu.<br><br>Vajíčka uvaříme natvrdo, pak odskořápkujeme.<br><br>Uvařené brambory oloupeme a protlačujeme kráječem na brambory.<br><br>To samé uděláme s vejcem, mrkví a celerem, pokud se nám nechce krájet.<br><br>Přidáme nakrájenou okurku, cibuli (může být sterilovaná) a hrášek.<br><br>Přidáme jogurt, majonézu, hořčici a dle chuti dosolíme a opepříme.<br><br>Připravené řízky můžeme smažit, mezitím necháme vychladnout a uležet salát.", obrazek: "<img src='../../img/maso/Rizek-SBramborovym-Salatem.webp'>" },
    {nazev: "Segedínský guláš", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>4 PORCE</b><br>- 500 g vepřového masa (plec nebo krkovice)<br>- 300 g kysaného zelí<br>- 1 střední cibule<br>- 2 stroužky česneku<br>- 1 lžíce sádla (nebo rostlinného oleje)<br>- 1 lžíce sladké mleté papriky<br>- 1/2 lžičky kmínu (mletého nebo celého)<br>- 1 bobkový list<br>- 1 lžička majoránky<br>- 1/2 lžičky mleté pálivé papriky (dle chuti)<br>- 200 ml zakysané smetany (nebo smetany ke šlehání)<br>- 2 lžíce hladké mouky (na zahuštění)<br>- Sůl a pepř dle chuti<br>- 500 ml vývaru (nebo vody)", postup: "<b><u>Postup přípravy:</b></u><br><br>Na sádle nebo oleji osmahněte nadrobno nakrájenou cibuli do zesklovatění. Přidejte nasekaný česnek a krátce orestujte. Vložte na kostky nakrájené vepřové maso a ze všech stran ho opečte, aby se zatáhlo. Zaprašte mletou paprikou (sladkou i pálivou), promíchejte a ihned podlijte trochou vývaru, aby paprika nezhořkla. Do základu přidejte kmín, bobkový list, majoránku, osolte a opepřete.<br><br>Maso zalijte vývarem tak, aby bylo ponořené, a nechte na mírném plameni dusit pod pokličkou asi 40–50 minut, dokud nezměkne. Během vaření občas promíchejte a případně dolijte vývar.<br><br>Jakmile je maso měkké, přidejte kysané zelí (můžete ho předtím lehce propláchnout, pokud je příliš kyselé). Vařte dalších 15–20 minut, aby se chutě propojily.<br><br>V misce rozmíchejte zakysanou smetanu s hladkou moukou a trochou horké šťávy z guláše. Tuto směs vmíchejte do hrnce a vše dobře provařte, aby se guláš zahustil. Podle potřeby dochuťte solí, pepřem nebo špetkou cukru, pokud je omáčka příliš kyselá.", obrazek: "<img src='../../img/maso/Segedinsky-Gulas.webp'>" },
    {nazev: "Sekaná", ingredience: "<b><u>Ingredience:</u></b><br><br>- mleté maso<br>- 1 malá cibule<br>- 3 stroužky česneku<br>- 2 lžičky hořčice<br>- strouhanka<br>- 1 vejce (žloutek)<br>- pepř<br>- sůl", postup: "<b><u>Postup přípravy:</b></u><br><br>Jemně nakrájenou cibuli, nastrouhaný česnek, vejce, hořčice, pepř, sůl a trochu strouhanky pořádně promícháme s masem.<br><br>Vytvoříme si bochánek do zapékací mísy, trošku zalijeme olejem, trochu podlijeme a zaprášíme strouhankou, která nám udělá kůrku.<br><br>Pečeme na 180°C dokud nám pěkně nezhnědne, což je tam 45 minut.<br><br>Podáváme s bramborem nebo chlebem.", obrazek: "<img src='../../img/maso/Sekana.webp'>" },
    {nazev: "Svíčková", ingredience: "<b><u>Ingredience:</u></b><br><br>- hovězí svíčková<br>- cibule<br>- sůl<br>- hladká mouka<br>- olej<br>- smetana<br>- bobkový list<br>- pepř<br>- kořenová zelenina<br>- cukr", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso na prudko osmažit a následně vložíme do zapékací mísy.<br><br>Přidáme kořenovou zeleninu, podlijeme a dusíme do změknutí.<br><br>Maso vyndáme, zeleninu rozmixujeme, zalijeme smetanou, dochutíme.", obrazek: "<img src='../../img/maso/Svickova.webp'>" },
    {nazev: "Sýrová omáčka", ingredience: "<b><u>Ingredience:</u></b><br><br>- špagety<br>- smetana na vaření<br>- niva<br>- cibule<br>- kuřecí maso", postup: "<b><u>Postup přípravy:</b></u><br><br>Cibuli nakrájíme najemno a necháme zesklovatět.<br><br>Maso nakrájené na kostky přidáme k cibuli a smažíme.<br><br>Mezitím si dáme vařit těstoviny a nastrouháme si nivu do nějaké misky.<br><br>Osmažené maso zasypeme nivou, kterou necháme roztavit a zalijeme smetanou.<br><br>Dochutíme klidně podravkou nebo jiným kořením dle chuti.", obrazek: "<img src='../../img/maso/Syrova-Omacka.webp'>" },
    {nazev: "Špagety", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>4 PORCE</b><br>- 400 g špaget<br>- mleté maso<br>- 2 lžíce olivového oleje<br>- 1 střední cibule (nadrobno nakrájená)<br>- 2 stroužky česneku (nasekané)<br>- 400 g konzervovaných rajčat (nebo rajčatového pyré)<br>- 1 lžíce rajčatového protlaku<br>- 1 lžička cukru (pro vyvážení chuti)<br>- 1/2 lžičky sušeného oregana (nebo tymiánu)<br>- 1/2 lžičky sušené bazalky<br>- parmezán (nastrouhaný, na posypání)<br>- sůl a pepř podle chuti<br>- čerstvá bazalka (na ozdobu)", postup: "<b><u>Postup přípravy:</b></u><br><br>Ve velkém hrnci přiveďte vodu k varu, osolte ji a uvařte špagety podle návodu na obalu tak, aby byly al dente (lehce na skus). Po uvaření špagety slijte, ale ponechte si asi 100 ml vody z vaření, kterou můžete přidat do omáčky.<br><br>Na pánvi rozehřejte olivový olej a osmahněte na něm nadrobno nakrájenou cibuli, dokud nezměkne. přidáme mleté maso, česnek a restujte krátce, aby se rozvoněl (pozor, aby se nespálil).<br><br>Vmíchejte rajčatový protlak a konzervovaná rajčata. Přidejte cukr, oregano, bazalku, sůl a pepř. Nechte omáčku vařit na mírném plameni asi 10–15 minut, aby se chutě spojily. Pokud je omáčka příliš hustá, přidejte trochu vody z vaření špaget.<br><br>Uvařené špagety vmíchejte přímo do omáčky na pánvi, aby se dobře obalily. Pokud chcete, můžete přidat trochu olivového oleje nebo strouhaného sýra pro krémovější konzistenci.<br><br>Podávejte na talíře, posypte čerstvě nastrouhaným parmezánem a ozdobte lístky bazalky.", obrazek: "<img src='../../img/maso/Spagety.webp'>" },
    {nazev: "Vepřové výpečky se špenátem a bramborovým knedlíkem", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>6 PORCÍ</b><br>- 1 kg vepřového masa plecko nebo bůček<br>- 1 lžíce kmín celý<br>- 2 ks cibule větší<br>- 4 stroužky česneku<br>- 1 lžíce hladká mouka<br>- 250 g mraženého špenátu protlačený<br>- 250 g mraženého špenátu sekaný<br>- 5 stroužků česneku protlačený<br>- 100 ml smetany na vaření<br>- 3 ks cibule<br>- 1 lžíce třtinového cukru<br>- 1 ks bramborového knedlíku v prášku", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso nakrájíme na větší kostky a smícháme v míse spolu s kmínem, cibulí a česnekem nakrájeným na kostičky, osolíme a opepříme.<br><br>Rozpálíme sádlo na pekáčku nebo pánvi a zprudka osmahneme. Až se maso zatáhne, podlijeme půhrnkem vody a dáme do trouby péct na 200° prvních 20 min zakryté pokličkou, pak odkryjeme a pečeme ještě alespoň další hodinu. Dle potřeby podlijeme půlhrnkem vody smíchanou s lžící mouky (aby se omáčka zahustila).<br><br>Mezitím co se peče maso si připravíme špenát a knedlíky. Špenáty necháme alespoň trochu rozmrazit. V hrnci začínáme ohřívat prolisovaný česnek a poté vložíme špenát. Zakryté dusíme 15 minut na mírném ohni, osolíme a opepříme dle potřeby, či ještě přidáme protlačený česnek navíc. Na posledních 5 minut zaliji smetanou na vaření.<br><br>Bramborové knedlíky připravíme dle popisu na krabičce. Kdo chce, může udělat vlastní.<br><br>Na pánvičce zvlášť si osmažím 3 nasekané cibule - zasypu je třtinovým cukrem, aby cibulka zkaramelizovala.<br><br>Servírujeme, výpekem přeliji knedlíky a posypu zkaramelizovanou cibulkou.", obrazek: "<img src='../../img/maso/Spenat-SMasem-ABrambor.webp'>" },
    {nazev: "Šunkofleky", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>6 PORCÍ</b><br>- 2 lžíce strouhanky<br>- 150 g sádla<br>- 300 g šunky od kosti<br>- pepř<br>- sůl<br>- 400 g flíčků<br>- 200 ml smetany ke šlehání (30%–33%)<br>- kyselé okurky<br>- 4 ks vajec<br>- 1 snítka hladkolisté petržele", postup: "<b><u>Postup přípravy:</b></u><br><br>Fleky uvaříme podle návodu na obalu. Pekáč vymažeme 20 g sádla a vysypeme strouhankou. Šunku nakrájíme na kostičky.<br><br>Scezené fleky promícháme v míse se šunkou, zbytkem sádla, solí a pepřem. Přendáme do připraveného pekáče.<br><br>Předpečeme 10 minut v troubě předehřáté na 180 °C. Mezitím v misce prošleháme smetanu s vejci, osolíme a opepříme. Vyndáme pekáč z trouby, fleky zalijeme vaječnou směsí a 20 minut dopečeme.<br><br>Podáváme posypané petrželkou a s okurkami ve sladkokyselém nálevu.", obrazek: "<img src='../../img/maso/Sunkofleky.webp'>" },
    {nazev: "Těstovinový salát", ingredience: "<b><u>Ingredience:</u></b><br><br>- 200 g kuřecích prsních řízků<br>- kostka kuřecího bujonu<br>- 400 g těstovin fusilli z pšenice Albert<br>- 2 lžíce rostlinného oleje<br>- 15 g plátků anglické slaniny<br>- červená cibule<br>- sůl<br>- 120 g bílého jogurtu<br>- 4 lžíce smetany na vaření (12 %)<br>- 2 lžíce olivového oleje<br>- citron<br>- pepř<br>- hrst čerstvé bazalky", postup: "<b><u>Postup přípravy:</b></u><br><br>Omytá kuřecí prsa vložte do vody, přidejte bujon a přiveďte k varu.<br><br>Na mírném ohni vařte 20 min, poté vyjměte a pomocí vidliček natrhejte na nudličky.<br><br>Mezitím uvařte těstoviny dle návodu na obalu a sceďte.<br><br>Rozpalte pánev s olejem, osmahněte slaninu nakrájenou na plátky, vsypte na nudličky pokrájenou cibuli, osolte a 5 min opékejte.<br><br>Vmíchejte těstoviny a odstavte z ohně.<br><br>V misce vyšlehejte dresink z jogurtu, smetany, olivového oleje, citronové šťávy, nadrobno nasekané bazalky, soli a pepře.<br><br>Salát podávejte s kuřecím masem a posypte lístky bazalky.", obrazek: "<img src='../../img/maso/Testovinovy-Salat.webp'>" },
    {nazev: "Vietnamské jarní závitky", ingredience: "<b><u>Ingredience:</u></b><br><br>- rýžové papírky<br>- rýžové nudle<br>- mleté maso<br>- 1 mrkev, nakrájená na jemné proužky<br>- česnek<br>- čínské zelí<br>- červená paprika, nakrájená najemno<br><br><b>&nbsp; &nbsp;NA OMÁČKU K NAMÁČENÍ</b><br>- 4 lžíce thajské rybí omáčky<br>- 2 lžíce rýžového octa<br>- střik citronové šťávy<br>- 1 čerstvá chilli paprička", postup: "<b><u>Postup přípravy:</b></u><br><br>Na oleji osmažíme na malé kostičky nakrájený česnek, přidáme k orestování mrkev strouhanou na struhadle a čínské zelí/klasické zelí na nudličky o max délce 3 cm.<br><br>Po chvíli přidáme nasolené a napepřené mleté maso.<br><br>Restujeme a snažíme se vařečkou maso rozdorbňovat na menší kousky.<br><br>Ke konci přidáme nasekaný čerstvý koriandr, dle potřeby dochutíme solí a na konec zahustíme solamylem, je-li směs moc řídká.<br><br>Vychladlou směs zabalíme do rýžových papírků.", obrazek: "<img src='../../img/maso/Vietnamske-jarni-zavitky.webp'>" },
    {nazev: "Zapečená krkovička", ingredience: "<b><u>Ingredience:</u></b><br><br>- krkovička<br>- 4 stroužky česneku<br>- cibule<br>- sůl<br>- pepř<br>- 6 lžic kečupu<br>- olej<br>- lžíce medu<br>- 2 lžíce sojové omáčky<br>- lžička sladké papriky", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso naklepeme, osolíme a vložíme na vymazaný plech.<br><br>Posypeme nakrájenou cibulí na měsíčky.<br><br>Připravíme marinádu ze surovin a zalijeme 250 ml vody a pečeme na 180°C 1,5 hod.", obrazek: "<img src='../../img/maso/Zapecena-Krkovicka.webp'>" },
    {nazev: "Zapečené brambory s kuřecím masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- kuřecí prsa<br>- brambory<br>- 1 cibule<br>- 1 balíček nivy<br>- 200 ml smetana<br>- sůl<br>- pepř<br>- grilovací koření", postup: "<b><u>Postup přípravy:</b></u><br><br>Můžeme si usnadnit práci a čas, že si předvaříme trochu celé brambory a osmažíme kuřecí prsa nebo oloupeme brambory, nakrájíme na plátky a první vrtstvou vyložíme dno zapékací mísy. Kuřecí prsa ochutíme solů, pepřem a klidně i grilovacím kořením a vložíme na vrstvu brambor. Cibuli oloupeme, rozpulíme, nakrájíme na plátky a poklademe do mísy na maso. Občas přidám kukuřici a česnek. Položíme další vrstvu brambor, okořeníme, trochu podlijeme smetanou, přikryjem a dáme péct do změknutí. Když už to vypadá hotově, posypeme brambory nastrouhanou nivou a dopečeme už odkryté a dle potřeby dolijeme smetanu. ", obrazek: "<img src='../../img/maso/Zapecene-Brambory.webp'>" },
    {nazev: "Zapečené těstoviny s mletým masem", ingredience: "<b><u>Ingredience:</u></b><br><br>- mleté maso <br>- těstoviny <br>- cibule<br>- česnek <br>- 1 mozarella <br>- Eidam ke strouhání <br>- rajčatová omáčka <br>- koření", postup: "<b><u>Postup přípravy:</b></u><br><br>V hrnci orestujeme cibulku a česnek. Posolíme. Přidáme mleté maso a vařečkou jej rozmělníme. Posolíme, přidáme oregano, červenou papriku a vše orestujeme. Směs zalijeme rajčatovou omáčkou a chvíli povaříme. Do hotové omáčky s masem přidáme uvařené těstoviny a vše promícháme. Směs vylijeme do zapékací mísy a smícháme s nastrouhánou mozarellou a sýrem. Vše dáme zapéct do trouby. ", obrazek: "<img src='../../img/maso/Testoviny-s-mletym-masem.webp'>" },
    {nazev: "Kuřecí maso s krevetamy a rýží", ingredience: "<b><u>Ingredience:</u></b><br><br>- cibule<br>- rýže<br>- kuřecí<br>- mražené krevety<br>- pórek, či jiná zelenina<br>- gyros koření<br>- Solamyl<br>- sójová omáčka<br>- čili omáčka", postup: "<b><u>Postup přípravy:</b></u><br><br>Dáme zesklovatět cibuli na oleji v hrnci. Přidáme propláchnutou rýži, nachvíli osmažíme a poté přilijeme vodu a vaříme do změknutí. Přidáme dle chuti Podravku a sůl. Případně hřebíček. Kuřecí maso naložené přes noc v oleji, sójové omáčce a gyros koření ještě před smažením posypeme Solamylem. Dáme smažit na pánev. Ke konci smažení ještě přidáme z mražáku krevety pokapané olejem a sójovou omáčkou. Směs restujeme, případně povaříme pod poklicí do zrůžovatění krevet. Maso a krevety vytáhneme, do rozpálené pánve přidáme nakrájený pórek, případně jinou zeleninu podle chuti. Restujeme. Následně přidáme masovou směs. Přidáme trochu vody, čili omáčku a Solamyl.", obrazek: "<img src='../../img/maso/Kure-ktevety-ryze.webp'>" },
    {nazev: "Kuřecí gyros s nudlemi", ingredience: "<b><u>Ingredience:</u></b><br><br>- 300 g stehenních kuřecích řízků<br>- 250g těstovin<br>- sůl<br>- sušený česnek<br>- mletá paprika<br>- 2 stroužky česneku<br>- 1 větší mrkev<br>- 1 červená cibule<br>- hrst jarní cibulky\n\n tmavá zálivka:<br>- 5 lžíce sójové omáčky Kikkoman<br>- 1 lžička srirachi<br>- 2 lžička rybí omáčky<br>- 1 lžička rýžového octu<br>- 1 lžička cukru<br>- 1 lžička sezamového oleje<br>- 1 lžička škrobu", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso nasolíme, přidámej sušený česnek a mletou sladkou papriku. Maso zprudka (nasucho) orestujeme z obou stran a dáme stranou. Do výpeku dáme plátky česneku, zarestujeme a poté přidáme nakrájenou mrkev, červenou cibuli a jarní cibulku, krátce zarestujeme a zalijeme připravenou tmavou zálivkou.Provaříme 1 minutu a poté do směsi přidáme uvařené těstoviny a nakrájené maso. Vše dobře promýcháme a servírujeme.", obrazek: "<img src='../../img/maso/Kureci-gyros.webp'>" },
    {nazev: "Kuře ve sladkokyselé omáčce", ingredience: "<b><u>Ingredience:</u></b><br><br>- 500 g kuřecích prs<br>- cibule<br>- paprika<br>- ananas<br>- rýže<br><br><b>Omáčka:</b><br>- 80 ml. vody<br>- 8 lžíce rajského protlaku (kečupu)<br>- 4 lžíce cukru (popř. dochutíme)<br>- 4 lžíce octu (rýžový nebo vinný)<br>- 3 lžíce sojové omáčky<br>- 0,5 lžičky soly<br><br><b>Těsto:</b><br>- 40 ml vody<br>- 2 lžíce hl. mouky<br>- 3 lžic škrobu<br>- 1 lžička soli<br>- 3 stroužek česneku<br>- 2 vejce<br>- 1 lžíce sojové omáčky", postup: "<b><u>Postup přípravy:</b></u><br><br>Maso nakrájíme na nudličky, vložíme do připraveného těsta a necháme uležet při pokojové teplotě. Nachystáme si pánev, do které nalijeme větší množství olej a vkládáme kousky naloženého masa, které restujeme do zlatova. Poté si vezmeme nejlépe vokovou pánev na které osmažíme na vetší kusy nakrájenou cibuli, papriku a nakonec přidáme kousky ananasu. Zalijeme připravenou omáčkou. Kdyby byla hodně řídká, přidáme ještě rozmíchaný škrob s vodou. Nakonec přidáme k zelenině maso a vypneme sporák. Podáváme s rýží a můžeme posipat ještě porkem nebo jarní cibulkou a sezamovým semínkem.", obrazek: "<img src='../../img/maso/Sladkokys.webp'>" },
    {nazev: "T-bone steak v rozmarýnovém oleji", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>1 PORCE</b><br>- 2 lžíce olivového oleje<br>- 1 hrs. čerstvého rozmarýnu<br>- 1 ks hovězího t-bone steaku<br>- 1 lžička soli<br>- 1 lžička pepře", postup: "<b><u>Postup přípravy:</b></u><br><br>Polovinu rozmarýnových snítek obereme a lístky naložíme na 30 minut do oleje.<br><br>Rozmarýnový olej vmasírujeme rukama do masa.<br><br>Maso položíme na rozpálený rošt a grilujeme 3–4 minuty z každé strany, chceme-li steak málo nebo středně propečený (vnitřní teplota masa 63 °C). Pro dobře propečený steak grilujeme z každé strany o 2 minuty déle (vnitřní teplota masa 77 °C).<br><br>Necháme chvíli odpočinout. Před podáváním osolíme, opepříme a ozdobíme zbývajícím rozmarýnem.", obrazek: "<img src='../../img/maso/T-steak.webp'>" },
    {nazev: "Grilovaná kuřecí křídla s koriandrovým dipem", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>4 PORCE</b><br>- 4 lžíce olivového oleje<br>- 600 g kuřecích křídel piri-piri<br>- 400 g zakysané smetany<br>- 2 stroužky česneku<br>- 1 cm zázvoru<br>- 1 hrs. koriandru<br>- sůl<br>- pepř<br>- 3 ks limetek", postup: "<b><u>Postup přípravy:</b></u><br><br>Grilovací rošt potřeme olivovým olejem, aby se křidélka nepřipálila.<br><br>Na rozpálený grilovací rošt položíme 600 g marinovaných piri-piri křídel a grilujeme je 10–13 minut z každé strany, dokud nebudou zlatavě hnědá a křupavá. V průběhu grilování je otáčíme, aby se rovnoměrně propekla. Po ugrilování necháme křídla 5 minut odpočinout na talíři. To pomůže masu vstřebat šťávu a zajistí šťavnatou chuť.<br><br>Na koriandrový dip smícháme v misce 400 g zakysané smetany se 2 oloupanými a prolisovanými stroužky česneku, asi centimetrem oloupaného a najemno nastrouhaného zázvoru, hrstí nasekaného a omytého koriandru, lžičkou olivového oleje a dochutíme solí, pepřem a čerstvě vymačkanou šťávou z jedné limety.<br><br>Kuřecí křídla podáváme s nakrájenými měsíčky dvou limet a vychlazeným koriandrovým dipem. Pozor, piri-piri křídla jsou extra pálivá a návyková pro milovníky ostrých chutí.", obrazek: "<img src='../../img/maso/Grilovana-kridla.webp'>" },
    {nazev: "Limetkové kuře v tequile se smetanovým dipem", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>4 PORCE</b><br>- 5 ks limetek<br>- 150 ml tequily<br>- 3 lžíce medu<br>- 4 stroužky česneku<br>- 1 lžičky mletého chilli<br>- sůl<br>- 100 g zakysané smetany<br>- 12 ks kuřecích stehen<br>- 180 g bílého jogurtu<br>- 1 ks červené cibule<br>- 1 lžíce třtinového cukru<br>- 1/2 lžičky sušené cibule<br>- 3/4 lžičky sušeného česneku<br>- 4 snítky čerstvého tymiánu<br>- 1 lžičky mleté sladké papriky", postup: "<b><u>Postup přípravy:</b></u><br><br>V misce vyšleháme šťávu ze 3 limetek, tequilu, med, rozmělněný česnek, chilli a 2 lžičky soli.<br><br>Kuřecí paličky vložíme do misky, přelijeme marinádou, zakryjeme a necháme 1 hodinu odležet v chladu.<br><br>V misce promícháme smetanu s jogurtem, polovinou najemno nakrájené cibule, cukrem, lístky ze dvou snítek tymiánu a kořením.<br><br>Na grilu rozehřátém na střední žár opečeme naložené kuřecí maso dozlatova, z každé strany 10 minut.<br><br>Upečené paličky podáváme s jogurtovým dipem, tymiánem a limetami, posypané kolečky cibule.", obrazek: "<img src='../../img/maso/Limetkove-kureci-palicky.webp'>" },
    {nazev: "Kuřecí špízy v marinádě pomodoro", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>4 PORCE</b><br>- 1 kg kuřecího steaku v marinádě pomodoro<br>- 12 ks rajčat cherry<br>- 1 ks cukety<br>- 10 ks ředkviček<br>- 2 lžíce olivového oleje<br>- 1 lžicka medu<br>- 1 ks citronu<br>- 1 snítka máty", postup: "<b><u>Postup přípravy:</b></u><br><br>Marinované kuřecí steaky nakrájíme na stejně velké kousky o velikosti 4 cm. Omyjeme 12 cherry rajčat a střídavě napichujeme kousek masa a rajčátko po jednom na špejle.<br><br><br>Opakujeme, dokud nezaplníme celou špejli.<br><br><br>Připravené špízy grilujeme na rozpáleném grilu 10–15 minut z každé strany a během grilování alespoň jednou potřeme olivovým olejem.<br><br>Na salátek si nejprve připravíme zeleninu. Škrabkou vytvoříme tenké plátky dobře omyté cukety, omyjeme 10 ředkviček a nakrájíme je na měsíčky. Na zálivku smícháme lžičku medu, lžíci olivového oleje a šťávu z poloviny citronu.<br><br>Grilované špízy podáváme se salátkem s lístky omyté máty.", obrazek: "<img src='../../img/maso/Kureci-spizy.webp'>" },
    {nazev: "Pikantní kuře", ingredience: "<b><u>Ingredience:</u></b><br><br>- 1 celé kuře<br>- 2 lžíce medu<br>- 150 ml černého piva<br>- kousek zázvoru<br>- 1 prolisovaný stroužek česneku<br>- 1 lžičku mleté papriky<br>- ½ lžičky mletého chilli<br>- sůl<br>- pepř<br>- 150 ml olivového oleje", postup: "<b><u>Postup přípravy:</b></u><br><br>Na marinádu smícháme v misce olej, pivo, koření, nastrouhaný zázvor a prolisovaný stroužek česneku. Kuře překrojíme na dvě poloviny a důkladně ho potřeme marinádou, vetřeme ji i pod kůži. Dáme do mísy, přilijeme zbylou marinádu, přikryjeme alobalem a necháme přes noc v chladu. Poté kuře pečeme na grilu. Obracíme ho a potíráme marinádou.", obrazek: "<img src='../../img/maso/pikantni-kure.webp'>" },
    {nazev: "Bylinkový losos", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>4 PORCE</b><br>- 800 g lososa s kůží<br>- 1 lžíci nasekané petrželky<br>- 1 chilli papričku<br>- 2 stroužky česneku<br>- 1 lžíci citronové šťávy<br>- 150 ml olivového oleje<br>- sůl<br>- pepř", postup: "<b><u>Postup přípravy:</b></u><br><br>Do misky nalijeme olej. Přidáme petrželku, nasekanou papričku, nadrcený česnek a citronovou šťávu. Směs dobře promícháme. Lososa osolíme a opepříme. Poté ho potřeme marinádou z obou stran. Opečeme ho několikrát krátce z obou stran a potíráme marinádou. Podáváme s domácími hranolky.", obrazek: "<img src='../../img/maso/Bylinkovy-losos.webp'>" },
    {nazev: "Plněná rajčata", ingredience: "<b><u>Ingredience na:</u></b><br><br><b>4 PORCE</b><br>- 4 velká masitá rajčata<br>- 1 misku uvařené rýže<br>- 100 g anglické slaniny<br>- 1 cibuli<br>- 1 stroužek česneku<br>- 4 snítky tymiánu<br>- olej<br>- 70 g nastrouhaného parmazánu", postup: "<b><u>Postup přípravy:</b></u><br><br>Rajčata seřízneme a vydlabeme. Vydlabanou dužninu pokrájíme. Na oleji opečeme najemno nakrájenou cibuli a na kostičky nakrájenou slaninu. Přidáme plátky česneku, lístky tymiánu a rajčatovou dužninu. Vše ještě chvíli opékáme.<br><br>Směs osolíme, opepříme a vmícháme rýži. Nakonec přidáme parmazán. Hotovou směsí naplníme rajčata. Přiklopíme seříznutou částí.<br><br>Pak je vyskládáme do olejem vymazaného pekáčku. Rajčata pečeme půl hodiny v troubě předehřáté na 180 °C.", obrazek: "<img src='../../img/maso/plnena-rajcata.webp'>" },

];



// Kopie pole jídla pro random výběr, kterou budeme upravovat
let kopieMaso = [...maso];

// Funkce pro náhodný výběr jídla
function vypisJidloARecept() {
    document.getElementById("reset").style.display = "none";       // skryje tlačítko - reset jídla
    document.getElementById("recept").style.display = "inherit";    // aktivuje skrytý obsah tabulky s receptem

    if (kopieMaso.length === 0) { // Když už není co vybírat
        document.getElementById("nazev").innerHTML = "už tady nic dalšího není 😮";
        document.getElementById("obrazek").innerHTML = "<img src='../../img/0pics/prazdny-talir.webp'>";
        document.getElementById("reset").style.display = "inherit";    // aktivuje skryté tlačítko - reset jídla
        document.getElementById("generuj").style.display = "none";     // skryje obsah tabulky s receptem
    } else {
        // Vybere náhodné jídlo z kopie seznamu
        let vypis = Math.floor(Math.random() * kopieMaso.length);
        document.getElementById("nazev").innerHTML = kopieMaso[vypis].nazev;
        document.getElementById("postup").innerHTML = kopieMaso[vypis].postup;
        document.getElementById("ingredience").innerHTML = kopieMaso[vypis].ingredience;
        document.getElementById("obrazek").innerHTML = kopieMaso[vypis].obrazek;

        kopieMaso.splice(vypis, 1); // Odstraní vybrané jídlo z kopie pole
    }
}

// Funkce pro načtení jídel do bočního panelu
function nactiJidla() {
    const somePlace = document.getElementById("myMenu");
    somePlace.innerHTML = ""; // Vyčistí seznam před novým naplněním

    maso.sort((a, b) => a.nazev.localeCompare(b.nazev)); // Seřadíme jídla podle názvu

    for (let i = 0; i < maso.length; i++) {
        const x = document.createElement("li");
        x.setAttribute("data-index", i);  // Použijeme data atribut pro odkazování na index v poli
        x.innerHTML = `<a href="#">${maso[i].nazev}</a>`;  // Naplní název jídla
        somePlace.appendChild(x);
    }
}

// Event listener pro kliknutí na položku v bočním panelu
document.getElementById("myMenu").addEventListener("click", function (e) {
    if (e.target && e.target.tagName === "A") { // Zkontroluje, zda byl kliknutý prvek odkaz
        e.preventDefault();
        const index = e.target.parentElement.getAttribute("data-index");  // Získáme index z data atributu
        const selectedJidlo = maso[index];  // Použijeme původní pole `maso`

        document.getElementById("nazev").innerHTML = selectedJidlo.nazev;
        document.getElementById("ingredience").innerHTML = selectedJidlo.ingredience;
        document.getElementById("postup").innerHTML = selectedJidlo.postup;
        document.getElementById("obrazek").innerHTML = selectedJidlo.obrazek;
        document.getElementById("recept").style.display = "inherit";  // Zobrazí obsah tabulky s receptem
    }
});

// Funkce pro resetování kopie pole pro random výběr (pokud je potřeba)
function resetJidla() {
    kopieMaso = [...maso]; // Resetuje kopii pole na původní obsah
    document.getElementById("generuj").style.display = "inherit"; // Znovu zobrazí tlačítko generování
    document.getElementById("reset").style.display = "none"; // Skryje tlačítko reset
}

// Inicializace bočního panelu po načtení stránky
window.onload = function() {
    nactiJidla();
};